<style lang="scss">
  section {
    position: relative;
    width: 100%;
    max-width: 100rem;
    padding: 10vh 0.5rem;
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
    color: var(--text-color);

    #carousel-navigation {
      display: none;
    }
    h1 {
      width: 100%;
      text-align: center;
      font-size: clamp(3rem, 10vw, 4.5rem);
      margin-bottom: 2.5rem;
      user-select: none;
    }
    .skills-wrapper {
      width: 100%;
      margin-bottom: 2rem;
      user-select: none;

      h2 {
        width: 100%;
        margin-bottom: 2rem;
        font-size: clamp(2rem, 5vw, 2.5rem);
        line-height: 1.3;
        text-align: center;
      }
      ul {
        list-style-type: none;
        padding: 0 2rem;
        display: flex;
        align-items: center;
        justify-content: space-around;
        flex-wrap: wrap;

        li {
          width: 10rem;
          height: 8.5rem;
          margin-bottom: 3rem;
          display: flex;
          align-items: center;
          justify-content: space-between;
          flex-direction: column;
          color: var(--text-color);

          svg {
            width: 5rem;
            height: 5rem;
          }
          h3 {
            font-size: clamp(1.6rem, 4vw, 2rem);
          }
        }
      }
    }
    @media (min-width: 800px) {
      #carousel-navigation {
        position: absolute;
        left: 50%;
        width: 100%;
        height: 5rem;
        transform: translateX(-50%);
        transition: transform 0.3s ease-in-out;
        display: flex;
        align-items: center;
        justify-content: space-between;
      }
      .arrow-button {
        cursor: pointer;
        height: 100%;
        width: 5rem;
        transition: color 0.2s ease-in-out;
        &:hover {
          color: var(--text-color-selected);
        }
        svg {
          width: 5rem;
          height: 5rem;
          display: flex;
          align-items: center;
          justify-content: center;
          transition: transform 0.2s ease-in-out;
        }
        &.prev:hover svg {
          transform: translateX(-0.7rem);
        }
        &.next:hover svg {
          transform: translateX(0.7rem);
        }
      }
      #skills-carousel {
        width: 100%;
        min-height: max(90vh, 80rem);
        transform-style: preserve-3d;
        perspective: 100px;

        .skills-wrapper {
          position: absolute;
          top: 0;
          left: 50%;
          width: 80%;
          width: clamp(40rem, 50vw, 70rem);
          height: 60%;
          height: clamp(45rem, 50vh, 50rem);
          margin: 0;
          background: var(--background);
          border-radius: 1rem;
          box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.7);
          transform: translate(-50%, -50%);
          transition: transform 0.3s ease-in-out;

          .skills-group {
            position: relative;
            width: 80%;
            width: clamp(40rem, 50vw, 70rem);
            height: 60%;
            height: clamp(45rem, 50vh, 50rem);
            padding: 2rem;
            display: flex;
            align-items: center;
            justify-content: center;
            flex-direction: column;
            background: var(--background-lightest);
            border-radius: 1rem;
            transition: opacity 0.3s ease-in-out;

            ul {
              height: 100%;
              width: 100%;
              li {
                margin: 0;
                flex-basis: 30%;
              }
            }
          }
        }
        .skills-wrapper.maincard {
          transform: translate(-50%, 20%);
        }
        .skills-wrapper.rightcard {
          transform: translate(-30%, 0%) translateZ(-1px);
          .skills-group {
            opacity: 0.4;
          }
        }
        .skills-wrapper.leftcard {
          transform: translate(-70%, 10%) translateZ(-2px);
          .skills-group {
            opacity: 0.1;
          }
        }
      }
    }
  }
</style>
